const ProjectSection = () => (
  <section className="items-center text-gray-600 body-font">
    <div className="items-center w-full container px-5 py-24 mx-auto">
      <div className="flex flex-wrap -mx-4 -mb-10">
        <div className="mb-10 mx-10 p-20 rounded-xl bg-[#c3ffe3]">
          <h2 className="w-full text-center mb-3 font-bold text-[#3a8561] text-4xl">
            Gender Inclusive Restrooms for Trans/Queer Persons
          </h2>
          <p className="leading-relaxed text-base">
            Indradhanu has been working with IIT Delhi to address the lack of any gender inclusive restrooms for
            public use on the IIT Delhi campus...
          </p>
          <img alt="content" className="object-cover object-center h-full w-full" src="assets/projects.png" />
          <p className="leading-relaxed text-base mt-4">
            Indradhanu in collaboration with IGES carried out a restroom accessibility assessment of all the public
            use restrooms in 7 major segments...
          </p>
          <button className="flex mx-auto mt-14 text-white bg-indigo-500 border-0 py-2 px-5 focus:outline-none hover:bg-indigo-600 rounded">
            <a href="https://instagram.com/indradhanu.iitd" target="_blank" rel="noreferrer">
              Know More
            </a>
          </button>
        </div>
      </div>
    </div>
  </section>
);

const Footer = () => (
  <footer>
    <div className="footer-left">
      <p>IIT Delhi, Hauz Khas</p>
      <img src="assets/indradhanu-logo.png" alt="Logo" />
      <p>
        <a href="mailto:indradhanu.iitdelhi@gmail.com">indradhanu.iitdelhi@gmail.com</a>
      </p>
    </div>
    <div className="footer-right">
      <img src="assets/iitdelhi.png" alt="IIT Delhi" />
    </div>
    <p className="copyright">&copy; 2024 Copyright: Indradhanu, IIT Delhi</p>
  </footer>
);

const App = () => (
  <div>
    {/* <Header /> */}
    <ProjectSection />
    {/* Add other sections like Timeline and History here as components */}
  </div>
);

export default App;
